import React, { useEffect } from 'react';
import {
  FitBox,
  Group,
  Image,
  Path,
  rect,
  SkColor,
  SkHostRect,
  Skia,
  useImage,
} from '@shopify/react-native-skia';
import {
  interpolate,
  useDerivedValue,
  useSharedValue,
  withDelay,
  withTiming,
} from 'react-native-reanimated';
import { isFunction } from 'lodash';

export function VisaCardsSpreadSkia({
  showImages,
  cardBackgroundColor,
  imageWidth,
  imageHeight,
  animateOnEntry = true,
}: {
  showImages: boolean;
  cardBackgroundColor: SkColor;
  imageWidth: number;
  imageHeight: number;
  animateOnEntry?: boolean;
}) {
  const animationState = useSharedValue(1);

  useEffect(() => {
    animationState.value = 0;
  }, [animationState]);

  const cardThreeProgress = useDerivedValue(() =>
    animateOnEntry ? withDelay(170, withTiming(animationState.value, { duration: 450 })) : 0,
  );
  const progress = useDerivedValue(() =>
    animateOnEntry ? withDelay(100, withTiming(animationState.value, { duration: 300 })) : 0,
  );
  const cardTwoProgress = useDerivedValue(() =>
    animateOnEntry ? withDelay(150, withTiming(animationState.value, { duration: 350 })) : 0,
  );

  const startAngle = 70;
  const cardOneOrigin = { x: 74.81, y: 346.97 };
  const cardTwoOrigin = { x: 182.81, y: 433.97 };
  const cardThreeOrigin = { x: 255.9, y: 532.58 };

  const cardOneBackgroundOrigin = { x: 107.02, y: 313.5 };
  const cardTwoBackgroundOrigin = { x: 156.5, y: 396 };
  const cardThreeBackgroundOrigin = { x: 295.5, y: 509.5 };

  const cardOneTransform = useDerivedValue(() => [
    { rotate: -(progress.value * startAngle * Math.PI) / 180 },
  ]);
  const cardTwoTransform = useDerivedValue(() => [
    {
      translateX: -interpolate(
        cardTwoProgress.value,
        [1, 0],
        [cardTwoOrigin.x - cardOneOrigin.x, 0],
      ),
    },
    {
      translateY: -interpolate(
        cardTwoProgress.value,
        [1, 0],
        [cardTwoOrigin.y - cardOneOrigin.y, 0],
      ),
    },
    { rotate: -(cardTwoProgress.value * startAngle * Math.PI) / 180 },
  ]);
  const cardThreeTransform = useDerivedValue(() => [
    {
      translateX: -interpolate(
        cardThreeProgress.value,
        [1, 0],
        [cardThreeOrigin.x - cardOneOrigin.x, 0],
      ),
    },
    {
      translateY: -interpolate(
        cardThreeProgress.value,
        [1, 0],
        [cardThreeOrigin.y - cardOneOrigin.y, 0],
      ),
    },
    { rotate: -(cardThreeProgress.value * startAngle * Math.PI) / 180 },
  ]);

  const cardOneBackgroundTransform = useDerivedValue(() => [
    {
      translateX: -interpolate(
        progress.value,
        [1, 0],
        [cardOneBackgroundOrigin.x - cardOneOrigin.x, 0],
      ),
    },
    {
      translateY: -interpolate(
        progress.value,
        [1, 0],
        [cardOneBackgroundOrigin.y - cardOneOrigin.y - 16, 0],
      ),
    },
    { rotate: -(progress.value * startAngle * Math.PI) / 180 },
  ]);
  const cardTwoBackgroundTransform = useDerivedValue(() => [
    {
      translateX: -interpolate(
        cardTwoProgress.value,
        [1, 0],
        [cardTwoBackgroundOrigin.x - cardOneOrigin.x + 48, 0],
      ),
    },
    {
      translateY: -interpolate(
        cardTwoProgress.value,
        [1, 0],
        [cardTwoBackgroundOrigin.y - cardOneOrigin.y - 16, 0],
      ),
    },
    { rotate: -(cardTwoProgress.value * startAngle * Math.PI) / 180 },
  ]);
  const cardThreeBackgroundTransform = useDerivedValue(() => [
    {
      translateX: -interpolate(
        cardThreeProgress.value,
        [1, 0],
        [cardThreeBackgroundOrigin.x - cardOneOrigin.x, 0],
      ),
    },
    {
      translateY: -interpolate(
        cardThreeProgress.value,
        [1, 0],
        [cardThreeBackgroundOrigin.y - cardOneOrigin.y - 32, 0],
      ),
    },
    { rotate: -(cardThreeProgress.value * startAngle * Math.PI) / 180 },
  ]);

  return (
    <VisaCardsSpreadCoordinateSystem targetWidth={imageWidth} targetHeight={imageHeight}>
      <Group>
        <Group transform={cardOneBackgroundTransform} origin={cardOneOrigin}>
          <VisaCardBlackBackgroundSkia color={cardBackgroundColor} />
        </Group>
        <Group transform={cardTwoBackgroundTransform} origin={cardTwoOrigin}>
          <VisaCardBlueBackgroundSkia color={cardBackgroundColor} />
        </Group>
        <Group transform={cardThreeBackgroundTransform} origin={cardThreeOrigin}>
          <VisaCardWhiteBackgroundSkia color={cardBackgroundColor} />
        </Group>

        <Group transform={cardOneTransform} origin={cardOneOrigin}>
          <VisaCardBlackSkia showBackgroundImage={showImages} />
        </Group>
        <Group transform={cardTwoTransform} origin={cardTwoOrigin}>
          <VisaCardBlueSkia showBackgroundImage={showImages} />
        </Group>
        <Group transform={cardThreeTransform} origin={cardThreeOrigin}>
          <VisaCardWhiteSkia showBackgroundImage={showImages} />
        </Group>
      </Group>
    </VisaCardsSpreadCoordinateSystem>
  );
}

export function VisaCardsSpreadCoordinateSystem({
  targetWidth,
  targetHeight,
  children,
}: {
  targetWidth: number;
  targetHeight: number;
  children: React.JSX.Element | ((rect: SkHostRect) => React.JSX.Element);
}) {
  const srcRect = rect(-80, 65, 747, 837);
  return (
    <FitBox src={srcRect} dst={rect(0, 0, targetWidth * 0.9, targetHeight * 0.9)} fit="cover">
      {isFunction(children) ? children(srcRect) : children}
    </FitBox>
  );
}

